import { Component } from '@angular/core';
import { Observable, of } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent{
  title = 'angular-observables';

  data: any[] = [];

  array1 = [1,3,5,7,9];
  array2 = ['A','B','C','D'];


  //1.CREATE AN OBSERVABLE using observable constructor

  // myObservable=new Observable((observer) =>{
  //   //observer.next([1,2,3,4,5]);

  //   // streaming of data
  // setTimeout(() => {observer.next(1)}, 1000);
  // setTimeout(() => {observer.next(2)}, 2000);
  // setTimeout(() => {observer.next(3)}, 3000);
  // //setTimeout(() => {observer.error(new Error('Something went wrong. Please try again later.'))},4000);
  // setTimeout(() => {observer.next(4)}, 4000);
  // setTimeout(() => {observer.next(5)}, 5000);
  // setTimeout(() => {observer.complete()}, 6000);
  // });

  //2.Creat an observable using 'of' operator of rxjs

  myObservable = of(this.array1)

  GetAsyncData(){

    //Observer
    //next,error,complete
    // this.myObservable.subscribe((val:any) =>{
    //     this.data.push(val);       
    // },
    //   (err) =>{
    //       alert(err.message);
    //   },
    // () => {
    //   alert('All the data is streamed!');
    // });

    this.myObservable.subscribe({
      next: (val: any) => {
        this.data.push(val);
        log
      },
      error(err){
        alert(err.message);
      },
      complete(){
        alert('All the data is streamed!');
      }
    })

  }
}
